mapper.select:
select distinct(tc.CAR_NO),tc.CAR_PLATE_NO,tc.CAR_BRAND_ID,tc.CAR_BRAND_NAME,tc.CAR_MODEL_ID,tc.CAR_MODEL_NAME,tc.CAR_PHOTO_URL1,tc.SEATING,tcs.POWER,tcs.MILEAGE,tcs.RANGE_MILEAGE from t_car tc,t_car_status tcs where tc.CAR_NO=tcs.CAR_NO AND tc.IS_DEDICATED = 0 AND tc.ONLINE_STATUS = 1
AND tc.SEATING = #{seaTing}
AND tc.DEVICE_ID != '0' AND tc.DEVICE_ID is not null AND tc.DEVICE_ID !='' AND tcs.USERAGE_STATUS = 0 AND (tcs.CAR_STATUS is null OR tcs.CAR_STATUS = 2) AND tcs.POWER >= #{power} AND tcs.LOCATION_PARK_NO = #{parkNo} ORDER BY tcs.POWER DESC

select distinct(tc.CAR_NO),tc.CAR_PLATE_NO,tc.CAR_BRAND_ID,tc.CAR_BRAND_NAME,tc.CAR_MODEL_ID,tc.CAR_MODEL_NAME,tc.CAR_PHOTO_URL1,tc.SEATING,tcs.POWER,tcs.MILEAGE,tcs.RANGE_MILEAGE from t_car tc,t_car_status tcs where tc.CAR_NO=tcs.CAR_NO AND (tc.DEDICATED_COMPANY_ID = #{companyId} or tc.DEDICATED_COMPANY_ID is null or tc.DEDICATED_COMPANY_ID ='') AND tc.ONLINE_STATUS= 1
AND tc.SEATING = #{seaTing}
AND tc.DEVICE_ID != '0' AND tc.DEVICE_ID is not null AND tc.DEVICE_ID !='' AND tcs.USERAGE_STATUS = 0 AND (tcs.CAR_STATUS is null OR tcs.CAR_STATUS = 2) AND tcs.POWER >= #{power} AND tcs.LOCATION_PARK_NO = #{parkNo} ORDER BY tcs.POWER DESC
