mapper.select:
select
from C_ADVERT
order by UPDATE_TIME desc LIMIT #{rowIndex},#{pageSize}

select
from C_ADVERT
order by UPDATE_TIME desc LIMIT #{pageNo},#{pageSize}

	select 
		*
	from C_ADVERT



				ADVERT_POSITION <> #{q.advertPosition}


            	AND TYPE=#{q.type}



			AND IS_DELETED=0
			AND CENSOR_STATUS = 1
			AND IS_AVAILABLE = 1 


	order by ADVERT_POSITION asc
	LIMIT #{rowIndex},#{pageSize}

select CONCAT_WS('/',#{2},ADVERT_PIC_URL) as advertPicUrl ,LINK_URL as linkUrl,ADVERT_NAME as advertName,TEXT as text,LINK_TYPE as linkType from C_ADVERT

	TYPE = #{1}
	AND ADVERT_POSITION = #{0}
	AND CENSOR_STATUS = 1
	AND IS_AVAILABLE = 1
	AND ADVERT_TYPE = #{3}

order by RANKING asc
LIMIT 5

SELECT ADVERT_NAME as advertName, ADVERT_PIC_URL as advertPicUrl, LINK_URL as linkUrl, LINK_TYPE as linkType, TEXT as text FROM c_advert
CENSOR_STATUS = 1 AND IS_AVAILABLE = 1 AND TYPE = #{2} AND ADVERT_TYPE = #{1} AND ADVERT_POSITION = #{0}
order by RANKING asc
