mapper.select:
select

from t_worker_order

	WORKER_ORDER_NO=#{0}

select
from t_worker_order
WORKER_ORDER_NO in #{item}

SELECT o.WORKER_ORDER_NO AS workerOrderNo, c.CAR_PLATE_NO AS carPlateNo, o.TYPE AS type, o.WORK_ORDER_STATUS AS workOrderStatus, s.POWER AS power, s.RANGE_MILEAGE AS rangeMileage, c.CAR_BRAND_NAME AS carBrandName, c.CAR_MODEL_NAME AS carModelName, p.ADDR_STREET AS parkName, o.SEND_TIME AS sendTime, c.CAR_PHOTO_URL1 AS carPhotoUrl1, 12756274*asin(Sqrt(power(sin((#{q.latitude}-s.LATITUDE)*0.008726646),2)+Cos(#{q.latitude}*0.0174533)*Cos(s.LATITUDE*0.0174533)*power(sin((#{q.longitude}-s.LONGITUDE)*0.008726646),2))) as distance
FROM t_worker_order o LEFT JOIN t_car c ON o.CAR_NO = c.CAR_NO LEFT JOIN t_car_status s ON o.CAR_NO = s.CAR_NO LEFT JOIN t_park p ON p.PARK_NO = s.LOCATION_PARK_NO
AND o.WORKER_ID=#{q.workerNo}
AND o.WORK_ORDER_STATUS=#{q.taskStatus}
AND o.TYPE=#{q.type}
ORDER BY o.SEND_TIME DESC
ORDER BY o.SEND_TIME ASC
ORDER BY s.POWER DESC
ORDER BY s.POWER ASC

choose.when:
SELECT * FROM (SELECT w.WORKER_ORDER_NO AS taskNo, c.CAR_PHOTO_URL1 AS photoUrl, w.WORK_ORDER_STATUS AS taskStatus, w.TYPE AS type, w.CREATE_TIME AS crTime, w.SEND_TIME AS sendTime, p1.PARK_NAME AS parkName, p1.ADDR_STREET AS addrStreet, c.CAR_PLATE_NO AS carPlateNo, c.CAR_BRAND_NAME AS carBrandName, c.CAR_MODEL_NAME AS carModelName, s.POWER AS power, s.RANGE_MILEAGE AS rangeMileage, 12756274*asin(Sqrt(power(sin((#{q.latitude}-s.LATITUDE)*0.008726646),2)+Cos(#{q.latitude}*0.0174533)*Cos(s.LATITUDE*0.0174533)*power(sin((#{q.longitude}-s.LONGITUDE)*0.008726646),2))) as distance FROM t_worker_order w LEFT JOIN t_car c ON c.CAR_NO = w.CAR_NO LEFT JOIN t_car_status s ON w.CAR_NO = s.CAR_NO LEFT JOIN t_park p1 ON p1.PARK_NO = s.LOCATION_PARK_NO
AND W.WORKER_ID=#{q.workerNo}
AND w.WORK_ORDER_STATUS=#{q.taskStatus}
ORDER BY sendTime DESC
ORDER BY sendTime ASC
ORDER BY power DESC
ORDER BY power ASC
) T1 UNION SELECT * FROM (SELECT k.CHECK_PLAN_NO AS taskNo, p.PARK_PIC_URL1 AS photoUrl, k.PLAN_STATUS AS taskStatus, k.PLAN_TYPE AS type, k.CREATE_TIME AS crTime, k.CREATE_TIME AS sendTime, p.PARK_NAME AS parkName, p.ADDR_STREET AS addrStreet, 'xunjian' AS carPlateNo, '' AS carBrandName, '' AS carModelName, '' AS power, '' AS rangeMileage, 12756274*asin(Sqrt(power(sin((#{q.latitude}-p.LATITUDE)*0.008726646),2)+Cos(#{q.latitude}*0.0174533)*Cos(p.LATITUDE*0.0174533)*power(sin((#{q.longitude}-p.LONGITUDE)*0.008726646),2))) as distance FROM t_check_plan k LEFT JOIN t_park p ON k.PARK_NO = p.PARK_NO
AND k.WORKER_ID like '%${q.workerNo}%'
AND k.PLAN_STATUS=#{q.taskStatus}
ORDER BY sendTime DESC
ORDER BY sendTime ASC
ORDER BY crTime DESC
ORDER BY power DESC
ORDER BY power ASC
) T2 LIMIT #{rowIndex},#{pageSize}

SELECT k.CHECK_PLAN_NO AS taskNo, p.PARK_PIC_URL1 AS photoUrl, k.PLAN_STATUS AS taskStatus, k.PLAN_TYPE AS type, k.CREATE_TIME AS sendTime, p.PARK_NAME AS parkName, p.ADDR_STREET AS addrStreet, 'xunjian' AS carPlateNo, '' AS carBrandName, '' AS carModelName, '' AS power, '' AS rangeMileage, 12756274*asin(Sqrt(power(sin((#{q.latitude}-p.LATITUDE)*0.008726646),2)+Cos(#{q.latitude}*0.0174533)*Cos(p.LATITUDE*0.0174533)*power(sin((#{q.longitude}-p.LONGITUDE)*0.008726646),2))) as distance FROM t_check_plan k LEFT JOIN t_park p ON k.PARK_NO = p.PARK_NO
AND k.WORKER_ID like '%${q.workerNo}%'
AND k.PLAN_STATUS=#{q.taskStatus}
ORDER BY sendTime DESC
ORDER BY sendTime ASC
LIMIT #{rowIndex},#{pageSize}

choose.otherwise:
SELECT w.WORKER_ORDER_NO AS taskNo, c.CAR_PHOTO_URL1 AS photoUrl, w.WORK_ORDER_STATUS AS taskStatus, w.TYPE AS type, w.SEND_TIME AS sendTime, p1.PARK_NAME AS parkName, p1.ADDR_STREET AS addrStreet, c.CAR_PLATE_NO AS carPlateNo, c.CAR_BRAND_NAME AS carBrandName, c.CAR_MODEL_NAME AS carModelName, s.POWER AS power, s.RANGE_MILEAGE AS rangeMileage, 12756274*asin(Sqrt(power(sin((#{q.latitude}-s.LATITUDE)*0.008726646),2)+Cos(#{q.latitude}*0.0174533)*Cos(s.LATITUDE*0.0174533)*power(sin((#{q.longitude}-s.LONGITUDE)*0.008726646),2))) as distance FROM t_worker_order w LEFT JOIN t_car c ON c.CAR_NO = w.CAR_NO LEFT JOIN t_car_status s ON w.CAR_NO = s.CAR_NO LEFT JOIN t_park p1 ON p1.PARK_NO = s.LOCATION_PARK_NO
AND W.WORKER_ID=#{q.workerNo}
AND w.WORK_ORDER_STATUS=#{q.taskStatus}
AND w.TYPE=#{q.taskType}
ORDER BY sendTime DESC
ORDER BY sendTime ASC
ORDER BY power DESC
ORDER BY power ASC
LIMIT #{rowIndex},#{pageSize}

mapper.select:
select DISTINCT two.START_PARK_NO from t_worker_order two,t_park tp

	two.START_PARK_NO=tp.PARK_NO

		AND tp.ADDR_REGION1_NAME=#{addrRegion1Name}


		AND tp.ADDR_REGION2_NAME=#{addrRegion2Name}


		AND tp.ADDR_REGION3_NAME=#{addrRegion3Name}

select count(*) as workerTakeNum,START_PARK_NO as parkNo from
t_worker_order two,t_park tp


	two.START_PARK_NO=tp.PARK_NO

		AND tp.ADDR_REGION1_NAME=#{addrRegion1Name}


		AND tp.ADDR_REGION2_NAME=#{addrRegion2Name}


		AND tp.ADDR_REGION3_NAME=#{addrRegion3Name}




GROUP BY START_PARK_NO

